import './components.css';
import React, { useState } from 'react';

export default function CommandBar({socket}) {
    const [command, setCommand] = useState('');

    const handleSendCommand = (e) => {
        e.preventDefault();
        setCommand('');
    }

    return (
        <div>
            <form onSubmit={handleSendCommand}>
                <input type="text" className="command_bar" value={command} onchange={(e) => setCommand(e.target.value)}></input><button className="send_button">Send</button>
            </form>
        </div>
    )
}
